import React from "react";

export default function Card({ pokemon, loading, infoPokemon }) {
  return (
    <>
      {loading ? (
        <h2><img src="https://tenor.com/view/loading-gif-hr-gif-23067502" alt="Loading..." width="500" height="600"/>
        <div class="tenor-gif-embed" data-postid="23067502" data-share-method="host" data-aspect-ratio="1" data-width="100%"><a href="https://tenor.com/view/loading-gif-hr-gif-23067502">Loading Gif Sticker</a>from <a href="https://tenor.com/search/loading-stickers">Loading Stickers</a></div> <script type="text/javascript" async src="https://tenor.com/embed.js"></script>
        </h2>
      ) : (
        pokemon.map((item) => {
          return (
            <div className="card" key={item.id} onClick={()=>infoPokemon(item)}>
              <h2>{item.id}</h2>
              <img src={`https://play.pokemonshowdown.com/sprites/ani/${item.name}.gif`} alt="h" /><br/>
              <h2>{item.name[0].toUpperCase().concat(item.name.substring(1))}</h2>
            </div>
          );
        })
      )}
    </>
  );
}
